import React from 'react';
import Slider from "react-slick";
import { Popup, Card, Image, Rating } from 'semantic-ui-react'


export const SlickComp = () => {
    const settings = {
        // className: "center",
        infinite: false,
        // centerPadding: "60px",
        slidesToShow: 5,
        // swipeToSlide: true,
        responsive: [
            {
            breakpoint: 1024,
            settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
            }
            },
            {
            breakpoint: 600,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
            }
            },
            {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
            }
        ]
      };
    return(
        <span>
            <Slider {...settings}>
            <div>
                <Popup
                    trigger={
                    <Card className="p-0">
                        <Image src='https://files.sirclocdn.xyz/sylva-demo/products/140121162720_131004151349sirclo_color-blue.jpg' />
                        <Card.Content>
                        <Card.Header>My Neighbor Totoro</Card.Header>
                        <Card.Description>
                            Two sisters move to the country with their father in order to be
                            closer to their hospitalized mother, and discover the surrounding
                            trees are inhabited by magical spirits.
                        </Card.Description>
                        </Card.Content>
                    </Card>
                    }
                >
                    <Popup.Header>User Rating</Popup.Header>
                    <Popup.Content>
                    <Rating icon='star' defaultRating={3} maxRating={4} />
                    </Popup.Content>
                </Popup>
            </div>
            <div>
                <h3>2</h3>
            </div>
            <div>
                <h3>3</h3>
            </div>
            <div>
                <h3>4</h3>
            </div>
            <div>
                <h3>5</h3>
            </div>
            <div>
                <h3>6</h3>
            </div>
            </Slider>
        </span>
    )
}
