import React from 'react';
import Slider from "react-slick";
import { Popup, Card, Image, Rating } from 'semantic-ui-react'
interface Props {

}

export const SlickComp: React.FC<Props> = ({}) => {
   
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 5,
        slidesToScroll: 4,
        autoplay: true,
        speed: 2000,
        autoplaySpeed: 2000,
        responsive: [
            {
            breakpoint: 1024,
            settings: {
                slidesToShow: 5,
                slidesToScroll: 4,
                infinite: true,
                dots: true
            }
            },
            {
            breakpoint: 600,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
            }
            },
            {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
            }
        ]
      };
    return(
        <span>
            <Slider {...settings}>
            {product.map((row, index)=>{
                return(
                    <div key={index} className="px-3">
                        <Popup
                            trigger={
                            <Card className="p-0">
                                <Image src={row.pict}/>
                                <Card.Content>
                                <Card.Header>{row.name}</Card.Header>
                                <Card.Description>
                                <del>Rp.{row.harga_lama}</del>
                                <strong className='text-danger'> Rp.{row.harga_baru}</strong>
                                </Card.Description>
                                </Card.Content>
                            </Card>
                            }
                        >
                            <Popup.Header>Rating</Popup.Header>
                            <Popup.Content>
                            <Rating icon='star' defaultRating={row.rating} maxRating={4} />
                            </Popup.Content>
                        </Popup>
                    </div>
                )
            })}
            </Slider>
        </span>
    )
}
