import React from 'react';
import Slider from "react-slick";
import { Popup, Card, Image, Rating } from 'semantic-ui-react'


export const SlickComp = () => {
    const settings = {
        dots: true,
        infinite: false,
        slidesToShow: 5,
        slidesToScroll: 4,
        responsive: [
            {
            breakpoint: 1024,
            settings: {
                slidesToShow: 5,
                slidesToScroll: 4,
                infinite: true,
                dots: true
            }
            },
            {
            breakpoint: 600,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
            }
            },
            {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
            }
        ]
      };
    return(
        <span>
            <Slider {...settings}>
            <div className="px-3">
                <Popup
                    trigger={
                    <Card className="p-0">
                        <Image src='https://s1.bukalapak.com/img/63740775661/large/Capture_Frency.png.webp' />
                        <Card.Content>
                        <Card.Header>My Neighbor Totoro</Card.Header>
                        <Card.Description>
                            Two sisters move to the 
                        </Card.Description>
                        </Card.Content>
                    </Card>
                    }
                >
                    <Popup.Header>User Rating</Popup.Header>
                    <Popup.Content>
                    <Rating icon='star' defaultRating={3} maxRating={4} />
                    </Popup.Content>
                </Popup>
            </div>
            <div>
                <h3>2</h3>
            </div>
            <div>
                <h3>3</h3>
            </div>
            <div>
                <h3>4</h3>
            </div>
            <div>
                <h3>5</h3>
            </div>
            <div>
                <h3>6</h3>
            </div>
            </Slider>
        </span>
    )
}
