import React from 'react';
import Slider from "react-slick";
import { Popup, Card, Image, Rating } from 'semantic-ui-react'


export const SlickComp = () => {
    const product = [{
        name:'Meja Belajar Murah',
        harga: '300,000',
        rating: '4',
        pict: 'https://s1.bukalapak.com/img/63740775661/large/Capture_Frency.png.webp'
    },{
        name:'Meja Belajar Murah',
        harga: '300,000',
        rating: '5',
        pict: 'https://s1.bukalapak.com/img/63740775661/large/Capture_Frency.png.webp'
    },{
        name:'Meja Belajar Murah',
        harga: '300,000',
        rating: '3',
        pict: 'https://s1.bukalapak.com/img/63740775661/large/Capture_Frency.png.webp'
    },{
        name:'Meja Belajar Murah',
        harga: '300,000',
        rating: '4',
        pict: 'https://s1.bukalapak.com/img/63740775661/large/Capture_Frency.png.webp'
    }]
    const settings = {
        dots: true,
        infinite: false,
        slidesToShow: 5,
        slidesToScroll: 4,
        responsive: [
            {
            breakpoint: 1024,
            settings: {
                slidesToShow: 5,
                slidesToScroll: 4,
                infinite: true,
                dots: true
            }
            },
            {
            breakpoint: 600,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
            }
            },
            {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
            }
        ]
      };
    return(
        <span>
            <Slider {...settings}>
            {product.map((row, index)=>{
                return(
                    <div key={index} className="px-3">
                        <Popup
                            trigger={
                            <Card className="p-0">
                                <Image src={row.pict}/>
                                <Card.Content>
                                <Card.Header>{row.name}</Card.Header>
                                <Card.Description>
                                {row.harga}
                                </Card.Description>
                                </Card.Content>
                            </Card>
                            }
                        >
                            <Popup.Header>Rating</Popup.Header>
                            <Popup.Content>
                            <Rating icon='star' defaultRating={row.rating} maxRating={4} />
                            </Popup.Content>
                        </Popup>
                    </div>
                )
            })}
            </Slider>
        </span>
    )
}
